import React from 'react';
import Grid from '@material-ui/core/Grid';
import { BUTTONS, SORT_DIRECTION } from 'shared/constants/stringConstantsSelectors';
import { CommonButton } from 'components/common/input/buttons/commonButton';
import { Sort } from 'components/domain/input/buttons/filters/sort';
import { DownArrow } from 'components/common/displayData/icons/downArrow';
import { SwapArrow } from 'components/common/displayData/icons/swapArrows';
import { Change } from 'components/common/displayData/icons/change';
import { UpArrow } from 'components/common/displayData/icons/upArrow';

export const CommonButtonCollection = props => {

    const {
        CHANGE_VIEW_TYPE,
        REAL_IMAGHES,
        PLACEHOLDER_IMAGES,
    } = BUTTONS;

    const {
        productType,
        realImages,
        sortDirection,
        cardView,
        toggleRealImages,
        toggleSortDirection,
        toggleView,
    } = props;

    const displayImageText = realImages ? PLACEHOLDER_IMAGES : REAL_IMAGHES;
    const displayImageButton = cardView ? null : <CommonButton onClick={toggleRealImages} text={displayImageText} endIcon={<SwapArrow />}/>

    const sortArrowIcon = sortDirection === SORT_DIRECTION.ASC 
        ? <DownArrow />
        : <UpArrow /> ;

    return (
        <Grid container spacing={1}>
            <CommonButton onClick={toggleView} text={CHANGE_VIEW_TYPE} primary endIcon={<Change />}/>
            {displayImageButton}
            <Sort productType={productType} handleSortChange={props.handleSortChange}/>
            <CommonButton onClick={toggleSortDirection} primary startIcon={sortArrowIcon}/>
        </Grid>

    );
};
