import React, { Component } from 'react';
import { ClientData } from 'shared/api/clientData';
import Typography from '@material-ui/core/Typography';
import { CollectionTable } from 'components/common/displayData/table/table';
import { DATA_ATTRIBUTES, SORT_DIRECTION, FILTER_LABELS } from 'shared/constants/stringConstantsSelectors';

const {
    PROD_COMMONS,
    PROD_ACTION_FIGURES,
    PROD_MOVIES,
    PROD_VIDEO_GAMES,
} = DATA_ATTRIBUTES;

export class CollectionList extends Component {

    constructor () {
        super();
        this.state = {
            filterGroup: undefined,
        };
        this.changeStateBasedOnProductType = this.changeStateBasedOnProductType.bind(this);
    }

    changeStateBasedOnProductType = (productType, filterGroup) => {
        this.setState({ filterGroup: filterGroup });
        this.props.changeProductType(productType);
    }

    getAllBlackSeries = () => {
        const allData = ClientData.getAllBlackSeriesData();
        this.props.setDisplayData(allData);
    }

    handleProductLine = event => {
        const {
            changeProductLine,
        } = this.props;

        const productLine = event.target.value;
        changeProductLine(productLine);
        this.handleFilterChange(ACTION_FIGURES, null, null, productLine);
    }

    handleProductType = event => {
        const productType = event.target.value;
        switch(productType) {
        case ALL:
            this.changeStateBasedOnProductType(ALL, undefined);
            this.handleFilterChange(ALL); 
            break;
        case ACTION_FIGURES:
            this.changeStateBasedOnProductType(ACTION_FIGURES, undefined);
            this.props.setDisplayData([]); 
            break;
        case LEGOS:
            this.changeStateBasedOnProductType(LEGOS);
            this.handleFilterChange(LEGOS);
            break;
        case MOVIES:
            this.changeStateBasedOnProductType(MOVIES, this.movieFilterGroup());
            this.handleFilterChange(MOVIES);
            break;
        case VEHICLES:
            this.changeStateBasedOnProductType(VEHICLES);
            this.handleFilterChange(VEHICLES);
            break;
        case VIDEO_GAMES:
            this.changeStateBasedOnProductType(VIDEO_GAMES, this.videoGameFilterGroup());
            this.handleFilterChange(VIDEO_GAMES);
            break;
        default: 
            this.changeStateBasedOnProductType(undefined, undefined);
            this.props.setDisplayData([]);
            break;
        }
    }

    createRequest = (productType, filterAttribute, filterValue, productLine) => {
        return {
            productLine: productLine,
            productType: productType,
            filter: {
                filterAttribute: filterAttribute ? filterAttribute : undefined,
                value: filterValue ? filterValue : undefined,
            },
        };
    }

    handleEventFilterChange = (event, productType, filterAttribute) => {
        this.handleFilterChange(productType, filterAttribute, event.target.value, this.props.productLine);
    };

    handleFilterChange = (productType, filterAttribute, filterValue, productLine) => {
        const request = this.createRequest(productType, filterAttribute, filterValue, productLine);
        ClientData.handleDataRequests(request)
            .then((data) => {
                this.props.setDisplayData(data);
            });
    };

    actionFigureFilterGroup = () => { 
        return <ActionFigureFilters
            productLine={this.props.productLine} 
            handleProductLine={this.handleProductLine}
            handleFilterChangeByAssortment={event => this.handleEventFilterChange(event, ACTION_FIGURES, PROD_ACTION_FIGURES.ASSORTMENT)}
            handleFilterChangeByCharacter={event => this.handleEventFilterChange(event, ACTION_FIGURES, PROD_COMMONS.NAME)}
            handleFilterChangeByContentSource={event => this.handleEventFilterChange(event, ACTION_FIGURES, PROD_ACTION_FIGURES.MOVIE_TV)} 
            handleFilterByGroup={event => this.handleEventFilterChange(event, ACTION_FIGURES, PROD_ACTION_FIGURES.GROUP)}
            handleFilterByVersion={event => this.handleEventFilterChange(event, ACTION_FIGURES, PROD_ACTION_FIGURES.VERSIONS)}
        />;
    }

    movieFilterGroup = () => { 
        return  <AllMovieFilters 
            handleFilterChangeByMovie={event => this.handleEventFilterChange(event, MOVIES, PROD_COMMONS.NAME)}
            handleFilterChangeByMovieSet={event => this.handleEventFilterChange(event, MOVIES, PROD_MOVIES.MOVIE_SET)}
            handleFilterChangeByFormat={event => this.handleEventFilterChange(event, MOVIES, PROD_MOVIES.MOVIE_FORMAT)}
        />;
    }

    videoGameFilterGroup = () => {
        return <SingleSelect 
            handleFilterChangeBySystem={event => this.handleEventFilterChange(event, VIDEO_GAMES, PROD_VIDEO_GAMES.SYSTEMS)} 
            inputLabel={FILTER_LABELS.SYSTEMS}
            menuItems={VIDEO_GAME_SYSTEMS}
        />;
    }

    toggleView = () => {
        const {
            cardView,
            enableViewTypeCard,
            enableViewTypeTable,
        } = this.props;

        cardView ? enableViewTypeTable() : enableViewTypeCard();
    };

    handleSortChange = newSortAttribute => {
        const { 
            changeCurrentSortAttribute,
            displayedData,
            sortDirection,
            sortAttribute,
        } = this.props;

        const sortAlpha = sortDirection === SORT_DIRECTION.ASC 
            ? displayedData.sort((a, b) => (a[newSortAttribute] > b[newSortAttribute]) ? -1 : 1)
            : displayedData.sort((a, b) => (a[newSortAttribute] > b[newSortAttribute]) ? 1 : -1);
        this.props.setDisplayData(sortAlpha);
        if(!isEqual(newSortAttribute, sortAttribute)){
            changeCurrentSortAttribute(newSortAttribute);
        }

    };

    handleSortChangeFromFilter = event => {
        const sortAttribute = camelCase(event.target.value);
        this.handleSortChange(sortAttribute);
    };

    toggleSort = () => {
        const { 
            sortAttribute,
            toggleSortDirection,
        } = this.props;

        toggleSortDirection();
        this.handleSortChange(sortAttribute);
    };

    render() {

        const {
            cardView,
            displayedData,
            productType,
            tableView,
            realImages,
            sortDirection,
            demoMode,
        } = this.props;

        const displayFilterGroup = productType === ACTION_FIGURES
            ? this.actionFigureFilterGroup() 
            : this.state.filterGroup;

        const collectionView = tableView 
            ?   <CollectionTable collectionData={displayedData}/>
            :   <Cards collectionData={displayedData} realImages={realImages} productType={productType}/>;

        return (
            <>
                {/* <Modal buttonName={'New Item'} customComp={<AddNewItemForm />}/> */}
                <CommonButtonCollection 
                    toggleView={this.toggleView}
                    toggleRealImages={this.props.toggleRealImages}
                    realImages={realImages} 
                    productType={productType}
                    handleSortChange={this.handleSortChangeFromFilter}
                    sortDirection={sortDirection}
                    toggleSortDirection={this.toggleSort}
                    cardView={cardView}
                />
                <br/>
                <CommonFilterCollection 
                    handleProductType={this.handleProductType}
                    filterGroup={displayFilterGroup}
                    demoMode={demoMode}
                />
                {displayedData ? collectionView : 'No Collection Found'}
                <Typography gutterBottom variant='h5' component='h2'>{displayedData.length}</Typography>
            </>

        );
    }
}
