import React from 'react';
import * as DOMAIN from 'shared/constants/domainConstantSelectors';
import { FILTER_LABELS } from 'shared/constants/stringConstantsSelectors';
import { PRODUCT_LINES } from 'shared/constants/domainConstantSelectors';
import { SingleSelect } from 'components/common/input/selects/singleSelect';
import { GROUP_NAMES } from 'shared/constants/domainConstantSelectors';
import { ALL_SOURCE_NAMES } from 'shared/constants/domainConstantSelectors';
import { CHARACTER_NAMES } from 'shared/constants/domainConstantSelectors';
import { VERSIONS } from 'shared/constants/domainConstantSelectors';
import { GET_SPECIFIC_ASSORTMENT } from 'shared/constants/domainConstantSelectors';

export const ActionFigureFilters = props => {

    const assortOptions = GET_SPECIFIC_ASSORTMENT(props.productLine);

    const displayAllFilters = props.productLine !== ACTION_FIGURE_PRODUCT_LINE.ALL.NAME
        ? <>
            <SingleSelect 
                handleFilterChange={props.handleFilterByGroup} 
                inputLabel={FILTER_LABELS.GROUP}
                menuItems={GROUP_NAMES}
            />
            <SingleSelect 
                handleFilterChange={props.handleFilterChangeByContentSource} 
                inputLabel={FILTER_LABELS.SOURCE}
                menuItems={ALL_SOURCE_NAMES}
            />
            <SingleSelect 
                handleFilterChange={props.handleFilterChangeByCharacter} 
                inputLabel={FILTER_LABELS.CHARACTERS}
                menuItems={CHARACTER_NAMES}
            />
            <SingleSelect 
                handleFilterChange={props.handleFilterChangeByAssortment} 
                inputLabel={FILTER_LABELS.ASSORTMENT}
                menuItems={assortOptions}
            />
        </>
        : null;

    const displayVersionFilter = props.productLine === ACTION_FIGURE_PRODUCT_LINE.BLACK_SERIES_6.NAME
        ?   <SingleSelect 
            handleFilterChange={props.handleFilterByVersion} 
            inputLabel={FILTER_LABELS.VERSIONS}
            menuItems={VERSIONS}
        />
        : null;

    return (
        <>
            <SingleSelect 
                handleFilterChange={props.handleProductLine} 
                inputLabel={FILTER_LABELS.PRODUCT_LINES}
                menuItems={PRODUCT_LINES}
            />
            {displayAllFilters}
            {displayVersionFilter}
        </>

    );
};
