import * as actions from 'store/userSettings/userSettingsActions';
import cloneDeep from 'lodash/cloneDeep';
import { initialState } from 'store/initialState';

export const userSettings = (state = initialState.userSettings, action) => {
    console.log(action.type)

    switch (action.type) {
    case actions.TOGGLE_REAL_IMAGES: {
        const cloneState = cloneDeep(state);
        cloneState.realImages = !cloneState.realImages;
        return cloneState;
    }
    case actions.ENABLE_VIEW_TYPE_CARD: {
        const cloneState = cloneDeep(state);
        cloneState.collection.viewType[action.payload] = true;
        cloneState.collection.viewType.tableView = false;
        return cloneState;
    }
    case actions.ENABLE_VIEW_TYPE_TABLE: {
        const cloneState = cloneDeep(state);
        cloneState.collection.viewType[action.payload] = true;
        cloneState.collection.viewType.cardView = false;
        return cloneState;
    }
    case actions.TOGGLE_SORT_DIRECTION: {
        const cloneState = cloneDeep(state);
        cloneState.sort = action.payload;
        return cloneState;
    }
    case action.TOGGLE_DEMO_MODE: {
        const cloneState = cloneDeep(state);
        console.log(cloneState.demo)
        cloneState.demo = !cloneState.demo;
        console.log(cloneState.demo)
        return cloneState;
    }
    default:
        return state;
    }
};
