import React, { useState } from 'react';
// import MultiSelect from 'components/common/input/selects/multiSelect';
import { FILTER_LABELS } from 'shared/constants/stringConstantsSelectors';
import { SingleSelect } from 'components/common/input/selects/singleSelect';
import * as DOMAIN from 'shared/constants/domainConstantSelectors';

export const AddNewItemForm = props => {
    const [productType, setProductType] = useState();

    const handleChange = event => {
        setProductType(event.target.value);
    };

    const displayRemainingFormBasedOnProductType = () => {



        return productType;
    };

    const remainingFormComponents = displayRemainingFormBasedOnProductType();

    return (
        <>
            <SingleSelect 
                handleFilterChange={handleChange} 
                inputLabel={FILTER_LABELS.PRODUCT_TYPE}
                menuItems={DOMAIN.PRODUCT_TYPE}
            /> 
            {remainingFormComponents}
        </>
    );
};
