import React from 'react';
import { FILTER_LABELS } from 'shared/constants/stringConstantsSelectors';
import * as DOMAIN from 'shared/constants/domainConstantSelectors';
import { SingleSelect } from 'components/common/input/selects/singleSelect';

export const AllMovieFilters = props => {
    return (
        <>
            <SingleSelect 
                handleFilterChange={props.handleFilterChangeByMovie} 
                inputLabel={FILTER_LABELS.MOVIES}
                menuItems={DOMAIN.MOVIE_NAMES}
            />
            <SingleSelect 
                handleFilterChange={props.handleFilterChangeByFormat} 
                inputLabel={FILTER_LABELS.MOVIE_FORMAT}
                menuItems={DOMAIN.MOVIE_FORMATS}
            />
            <SingleSelect 
                handleFilterChange={props.handleFilterChangeByMovieSet} 
                inputLabel={FILTER_LABELS.MOVIE_SETS}
                menuItems={MOVIE_SETS}
            />
        </>

    );
};
