import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';

export const CommonButton = props => {

    const classes = makeStyles(theme => ({
        button: {
            margin: theme.spacing(1),
        },
    }));

    const displayColor = props.primary ? 'primary' : 'secondary';

    return (
        <Grid item xs={2}>
            <Button variant='contained' color={displayColor} className={classes.button} onClick={props.onClick} startIcon={props.startIcon} endIcon={props.endIcon}>{props.text}</Button>
        </Grid>     
    );
};
