import { toast as notificationCreator } from 'react-toastify';

export const ADD_NOTIFICATION = '[notifications]ADD_NOTIFICATION';
export const addNotificationAction = (id, type, message) => dispatch => {
    const action = {
        id: id,
        type: type, 
        message: message,
    };
    dispatch(_addNotificationAction(action));
    displayNotification(action);
};

const _addNotificationAction = action => ({
    type: ADD_NOTIFICATION,
    payload: action,
});

const displayNotification = notification => {
    console.log(notification)
    switch (notification.type) {
    case 'Info':

        notificationCreator.info("Info Notification !", {
            position: notificationCreator.POSITION.TOP_RIGHT,
        });
        break;
    default:
        break;
    }
};
