import { getSortDirection, getDemoMode } from 'store/userSettings/userSettingsSelector';
import { SORT_DIRECTION } from 'shared/constants/stringConstantsSelectors';

export const TOGGLE_REAL_IMAGES = '[userSettings]TOGGLE_REAL_IMAGES';
export const toggleRealImages = isRealImage => ({
    type: TOGGLE_REAL_IMAGES,
    payload: isRealImage,
});

export const ENABLE_VIEW_TYPE_CARD = '[userSettings]ENABLE_VIEW_TYPE_CARD';
export const enableViewTypeCard = cardView => ({
    type: ENABLE_VIEW_TYPE_CARD,
    payload: cardView,
});

export const ENABLE_VIEW_TYPE_TABLE = '[userSettings]ENABLE_VIEW_TYPE_TABLE';
export const enableViewTypeTable = tableView => ({
    type: ENABLE_VIEW_TYPE_TABLE,
    payload: tableView,
});

export const TOGGLE_SORT_DIRECTION = '[userSettings]TOGGLE_SORT_DIRECTION';
export const toggleSortDirection = () => (dispatch, getState) => {
    const currentSortDirection = getSortDirection(getState());
    const newSortDirection = currentSortDirection === SORT_DIRECTION.ASC ? SORT_DIRECTION.DESC : SORT_DIRECTION.ASC;
    dispatch(_toggleSortDirection(newSortDirection));
};

const _toggleSortDirection = direction => ({
    type: TOGGLE_SORT_DIRECTION,
    payload: direction,
});

export const TOGGLE_DEMO_MODE = '[userSettings]TOGGLE_DEMO_MODE';
export const toggleDemoMode = () => ({
    type: TOGGLE_DEMO_MODE,
    payload: demoMode,
});
