import React, { Component } from 'react';
import { ClientData } from 'shared/api/clientData';
import { WatchListTable } from 'components/watchlist/table';

export class Dashboard extends Component {

    constructor() {
        super();
        this.state = {
            data: this.data(),
        }
    }

    data = () => {
        ClientData.handleDataRequests()
            .then((data) => {
                this.props.setDisplayData(data);
            });
    }

    render() {

        const {
            displayedData,
        } = this.props;

        return (
            <FlexboxContainer>
                <WatchListTable data={displayedData}/>
            </FlexboxContainer>

        );
    }
}
