import React from 'react';
import { CharacterFilter } from 'components/domain/input/buttons/filterButtonGroups/common/characterFilter';
import { ContentSourceFilter } from 'components/domain/input/buttons/filterButtonGroups/common/contentSourceFilter';
import { VersionsFilters } from 'components/domain/input/buttons/filterButtonGroups/actionFigures/versionsFilter';
import { ACTION_FIGURE_PRODUCT_LINE } from 'shared/constants/domainConstantSelectors';
import { AssortmentFilters } from 'components/domain/input/buttons/filterButtonGroups/actionFigures/assortmentFilters';
import { FILTER_LABELS } from 'shared/constants/stringConstantsSelectors';
import { PRODUCT_LINES } from 'shared/constants/domainConstantSelectors';
import { SingleSelect } from 'components/common/input/selects/singleSelect';
import { GROUP_NAMES } from 'shared/constants/domainConstantSelectors';

export const ActionFigureFilters = props => {

    const displayAllFilters = props.productLine !== ACTION_FIGURE_PRODUCT_LINE.ALL.NAME
        ? <>
            <SingleSelect 
                handleFilterChange={props.handleFilterByGroup} 
                inputLabel={FILTER_LABELS.GROUP}
                menuItems={GROUP_NAMES}
            />
                        <SingleSelect 
                handleFilterChange={props.handleFilterChangeByContentSource} 
                inputLabel={FILTER_LABELS.SOURCE}
                menuItems={ALL_SOURCE_NAMES}
            />
            <CharacterFilter handleFilterChangeByCharacter={props.handleFilterChangeByCharacter}/>
            <AssortmentFilters 
                handleFilterChangeByAssortment={props.handleFilterChangeByAssortment}
                productLine={props.productLine}
            />
        </>
        : null;

    const displayVersionFilter = props.productLine === ACTION_FIGURE_PRODUCT_LINE.BLACK_SERIES_6.NAME
        ? <VersionsFilters handleFilterByVersion={props.handleFilterByVersion} /> 
        : null;

    return (
        <>
            <SingleSelect 
                handleFilterChange={props.handleProductLine} 
                inputLabel={FILTER_LABELS.PRODUCT_LINES}
                menuItems={PRODUCT_LINES}
            />
            {displayAllFilters}
            {displayVersionFilter}
        </>

    );
};
