/* eslint-disable react-hooks/rules-of-hooks */
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { CollectionList } from 'components/collectionList';
import { toggleRealImages, enableViewTypeCard, enableViewTypeTable, toggleSortDirection } from 'store/userSettings/userSettingsActions';
import { useRealImages, getCardViewType, getTableViewType, getSortDirection, getDemoMode } from 'store/userSettings/userSettingsSelector';
import { getSortAttribute, getProductType, getProductLine, getDisplayedData } from 'store/dataSet/dataSetSelector';
import { changeCurrentSortAttribute, changeProductType, changeProductLine, setDisplayData } from 'store/dataSet/dataSetActions';
import { compose } from 'redux';
import HotKeys from 'components/higherOrderComponents/hotKeys';
import { AppHotKeys } from 'components/hotKeys/appHotKeys';
import { ToastContainer as NotificationContainer } from 'react-toastify';


export class CollectionListConnect extends Component {
    render() {
        return (
            <>
                <NotificationContainer />
                <CollectionList />
            </>
        );
    }
}

export const mapStateToProps = state => ({
    realImages: useRealImages(state),
    cardView: getCardViewType(state),
    tableView: getTableViewType(state),
    sortDirection: getSortDirection(state),
    sortAttribute: getSortAttribute(state),
    productType: getProductType(state),
    productLine: getProductLine(state),
    displayedData: getDisplayedData(state),
    demoMode: getDemoMode(state),
});

export const mapDispatchToProps = dispatch => ({
    toggleRealImages: () => dispatch(toggleRealImages()),
    enableViewTypeCard: () => dispatch(enableViewTypeCard('cardView')),
    enableViewTypeTable: () => dispatch(enableViewTypeTable('tableView')),
    toggleSortDirection: () => dispatch(toggleSortDirection()),
    changeCurrentSortAttribute: sortAttribute => {
        dispatch(changeCurrentSortAttribute(sortAttribute));
    },
    changeProductType: productType => {
        dispatch(changeProductType(productType));
    },
    changeProductLine: productLine => {
        dispatch(changeProductLine(productLine));
    },
    setDisplayData: data => { 
        dispatch(setDisplayData(data));
    },
});

export const hotKeyConnect = compose(
    HotKeys(AppHotKeys.KeyMap, AppHotKeys.Handlers),
    connect(mapStateToProps, mapDispatchToProps),
);

export default hotKeyConnect(CollectionList);
