import React from 'react';
import Grid from '@material-ui/core/Grid';
import { FILTER_LABELS, PROD_MOVIES, PROD_COMMONS, PROD_ACTION_FIGURES, PROD_VIDEO_GAMES, PROD_LEGOS } from 'shared/constants/stringConstantsSelectors';
import { PRODUCT_TYPE_DETAILS } from 'shared/constants/domainConstantSelectors';
import { SingleSelect } from 'components/common/input/selects/singleSelect';
import { uncamelCaseArray } from 'shared/utilities/ut'


export const Sort = props => {

    const {
        ALL,
        ACTION_FIGURES,
        LEGOS,
        MOVIES,
        VIDEO_GAMES,
    } = PRODUCT_TYPE_DETAILS;

    const determineSortAttributes = productType => {
        switch(productType) {
        case ALL:
            return [];
        case ACTION_FIGURES:
            return uncamelCase(PROD_COMMONS.concat(PROD_ACTION_FIGURES));
        case LEGOS:
            return uncamelCase(PROD_COMMONS.concat(PROD_LEGOS));
        case MOVIES:
            return uncamelCase(PROD_COMMONS.concat(PROD_MOVIES));
        case VIDEO_GAMES:
            return uncamelCase(PROD_COMMONS.concat(PROD_VIDEO_GAMES));
        default: 
            return [];
        }
    };


    const sortAttributes = determineSortAttributes(props.productType);

    return (
        <Grid item xs={2}>
            <SingleSelect 
                handleFilterChange={props.handleSortChange} 
                inputLabel={FILTER_LABELS.SORT_BY}
                menuItems={sortAttributes}
            />
        </Grid>
    );
};
