import React, { Component } from 'react';
import { API } from 'shared/api/api';
import { WatchListTable } from 'components/watchlist/table';
import { FlexboxContainer } from 'shared/styles/flexboxContainer';
import { QuoteDetail } from 'components/quoteDetail/quoteDetail';
import { WATCHLIST_TABLE_NAME } from 'shared/constants/stringConstantsSelectors';

export class Dashboard extends Component {

    constructor() {
        super();
        this.state = {
            data: this.data(),
        }
    }

    data = () => {
        API.handleDataRequestsAPI()
            .then((data) => {
                this.props.setDisplayData(data);
            });
    }

    render() {

        const {
            displayedData,
        } = this.props;

        const quoteDetailRecord = displayedData.length > 0 && displayedData[0];

        return (
            <FlexboxContainer>
                <div style={{width: '40%', margin: '1%'}}>
                    <QuoteDetail record={quoteDetailRecord}/>
                </div>
                <div style={{width: '80%', margin: '1%'}}>
                    <h2></h2>/h2>
                    <WatchListTable data={displayedData}/>
                </div>
            </FlexboxContainer>

        );
    }
}
