import { toast as notificationCreator } from 'react-toastify';
import { NOTIFICATION_TYPES } from 'shared/constants/stringConstantsSelectors';

export const ADD_NOTIFICATION = '[notifications]ADD_NOTIFICATION';
export const addNotificationAction = (id, type, message) => dispatch => {
    const action = {
        id: id,
        type: type, 
        message: message,
    };
    dispatch(_addNotificationAction(action));
    displayNotification(action);
};

const _addNotificationAction = action => ({
    type: ADD_NOTIFICATION,
    payload: action,
});

const displayNotification = notification => {
    switch (notification.type) {
    case NOTIFICATION_TYPES.INFO:
        notificationCreator.info(notification.message);
        break;
    default:
        break;
    }
};

export const OPEN_NOTIFCIATION_DRAWER = '[notifications]OPEN_NOTIFCIATION_DRAWER';
export const openNotficationDrawer = () => ({
    type: OPEN_NOTIFCIATION_DRAWER,
    payload: true,
});

export const CLOSE_NOTIFCIATION_DRAWER = '[notifications]CLOSE_NOTIFCIATION_DRAWER';
export const closeNotficationDrawer = () => ({
    type: CLOSE_NOTIFCIATION_DRAWER,
    payload: false,
});
