import React from 'react';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { NewInBoxIcon } from '../../../../common/displayData/icons/newInBoxIcon';
import { IMAGES } from '../../../../../shared/constants/stringConstantsSelectors';

export const MovieItem = props => {

    const {
        id,
        name,
        lucasFormat,
        mediaFormat,
        screenFormat,
        imageURL,
        bought,
        set,
    } = props.item;

    const imageSource = props.realImages ?  imageURL : IMAGES.FILL_MURRAY;

    return (
        <div>
            { props.item ? (
                <Card style={{ margin: 5 }}>
                    <CardMedia 
                        style={{ paddingTop: '60%', height: '200px', textAlign: 'top' }}    
                        image={imageSource}
                        title={id}
                    />
                    <CardContent>
                        <Typography gutterBottom variant='h5' component='h2'>{name}</Typography>
                        {mediaFormat} - {screenFormat}
                        <Typography component='p'>{lucasFormat}</Typography>
                        <Typography component='p'>{set}</Typography>
                    </CardContent>
                    <CardActions>
                        <Button size='small' color='primary' href={imageURL} target='_blank'>Image +</Button>
                        {bought && <NewInBoxIcon color="primary" />}
                    </CardActions>
                </Card>
            ) : null}
        </div>
    );
};
