import * as actions from 'store/features'
import cloneDeep from 'lodash/cloneDeep';
import { initialState } from 'store/initialState';

export const features = (state = initialState.features, action) => {
    switch (action.type) {
    case actions.ENABLE_FEATURE_ACTION: {
        const cloneState = cloneDeep(state);
        cloneState.features[action.payload] = true;
        return cloneState;
    }
    case actions.DISABLE_FEATURE_ACTION: {
        const cloneState = cloneDeep(state);
        cloneState.features[action.payload] = false;
        return cloneState;
    }
    default:
        return state;
    }
};
