import * as actions from 'store/notifications/notificationActions';
import { initialState } from 'store/initialState';
import cloneDeep from 'lodash/cloneDeep';

export const notifications = (state = initialState.notifications, action) => {
    switch (action.type) {
    case actions.ADD_NOTIFICATION: {
        let cloneState = cloneDeep(state);
        if (cloneState.list){
            cloneState.list.push(action.payload);
        } else {
            cloneState.list = [action.payload];
        }
        return cloneState;
    }
    case actions.OPEN_NOTIFCIATION_DRAWER:
    case actions.CLOSE_NOTIFCIATION_DRAWER: {
        return Object.assign({}, state, { isOpen: action.payload });
    }
    default:
        return state;
    }
};
