import { API } from 'shared/api/api';

export class ClientData {

    static handleDataRequests = request => {

        let promise = new Promise(function(resolve, reject) {
            const data = API.handleDataRequestsAPI(request);
            data && resolve();
            !data && reject();
        });




        return promise.then(API.handleDataRequestsAPI(request)).then(response => response.data);
    }
}
