import reduce from 'lodash/reduce';
import { toggleDemoMode } from 'store/userSettings/userSettingsActions';

export class AppHotKeys {
    static KeyMapWithDetails = [
        {
            name: 'HotKeys',
            children: [
                {
                    name: 'DemoMode',
                    handler: 'toggleDemoMode',
                    hotKey: ['d m', 'm d'],
                },
                {
                    name: 'enable',
                    handler: 'toggleDemoMode',
                    hotKey: ['d m', 'm d'],
                },
            ],
        },
    ];

    static KeyMap = reduce(AppHotKeys.KeyMapWithDetails.map(item => {
        if ('children' in item) {
            return reduce(item.children, (acc, child) => Object.assign(acc, { [child.handler]: child.hotKey }), {});
        }
        return { [item.handler]: item.hotKey };
    }), (acc, item) => Object.assign(acc, item), {});


    static Handlers = (dispatch, getState) => {
        return {
            'toggleDemoMode': () => {
                dispatch(toggleDemoMode());
            },
        };
    };
}
