import React from 'react';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { NewInBoxIcon } from 'components/common/displayData/icons/newInBoxIcon';
import { IMAGES } from 'shared/constants/stringConstantsSelectors';

export const VideoGameItem = props => {

    const {
        id,
        imageURL,
        name,
        systems,
        boxCase,
        year,
    } = props.item;

    const imageSource = props.realImages ?  imageURL : IMAGES.FILL_MURRAY;

    const systemsDisplay = systems.map(item => (
        <Typography component='p'><li>{item}</li></Typography>
    ));


    return (
        <div>
            { props.item ? (
                <Card style={{ margin: 5 }}>
                    <CardMedia 
                        style={{ paddingTop: '60%', height: '50px', textAlign: 'top' }}    
                        image={imageSource}
                        title={id}
                    />
                    <CardContent>
                        <Typography gutterBottom variant='h5' component='h2'>{name}</Typography>
                        <Typography component='p'>{year}</Typography>
                        {systemsDisplay}
                    </CardContent>
                    <CardActions>
                        <Button size='small' color='primary' href={imageURL} target='_blank'>Image +</Button>
                        {boxCase && <NewInBoxIcon color="primary" />}
                    </CardActions>
                </Card>
            ) : null}
        </div>
    );
};
