import React from 'react';
import { FILTER_LABELS } from 'shared/constants/stringConstantsSelectors';
import { MOVIE_NAMES } from 'shared/constants/domainConstantSelectors';
import { SingleSelect } from 'components/common/input/selects/singleSelect';
import { MOVIE_FORMATS } from 'shared/constants/domainConstantSelectors';
import { MovieFormatFilter } from 'components/domain/input/buttons/filterButtonGroups/mediaFilters/movieFormatFilter';

export const AllMovieFilters = props => {
    return (
        <>
            <SingleSelect 
                handleFilterChange={props.handleFilterChangeByMovie} 
                inputLabel={FILTER_LABELS.MOVIES}
                menuItems={MOVIE_NAMES}
            />
            <SingleSelect 
                handleFilterChange={props.handleFilterChangeByFormat} 
                inputLabel={FILTER_LABELS.MOVIE_FORMAT}
                menuItems={MOVIE_FORMATS}
            />
            <MovieSetFilters handleFilterChangeByMovieSet={props.handleFilterChangeByMovieSet} />
        </>

    );
};
