import { PRODUCT_TYPE_DETAILS, PRODUCT_LINES_OBJ } from 'shared/constants/domainConstantSelectors';
import { DATA_ATTRIBUTES } from 'shared/constants/stringConstantsSelectors';

import { BlackSeries2013n2014 } from 'data/toys/actionFigures/blackSeries/blackSeries2013n2014';
import { BlackSeries2014n2015 } from 'data/toys/actionFigures/blackSeries/blackSeries2014n2015';
import { BlackSeries2015nPresent } from 'data/toys/actionFigures/blackSeries/blackSeries2015nPresent';
import { BlackSeriesCenterpiece } from 'data/toys/actionFigures/blackSeries/blackSeriesCenterpiece';
import { BlackSeriesDeluxVehicle } from 'data/toys/actionFigures/blackSeries/blackSeriesDeluxVehicle';
import { BlackSeriesExclusives } from 'data/toys/actionFigures/blackSeries/blackSeriesExclusives';
import { BlackSeriesArchive } from 'data/toys/actionFigures/blackSeries/blackSeriesArchive';

import { EliteSeries } from 'data/toys/actionFigures/eliteSeries/eliteSeries';

import { ActionFleet } from 'data/toys/vehicles/actionFleet';

import { Legos } from 'data/toys/legos';
import { Movies } from 'data/media/movies';
import { VideoGames } from 'data/media/videoGames';

const {
    ALL,
    ACTION_FIGURES,
    LEGOS,
    MOVIES,
    VEHICLES,
    VIDEO_GAMES,
} = PRODUCT_TYPE_DETAILS;

export class API {

    static handleDataRequestsAPI = request => {
        const { 
            productLine,
            productType, 
            filter, 
        } = request;

        const { 
            filterAttribute, 
            value, 
        } = filter;

        switch(productType) {
        case ALL:
            return API.getAllData();
        case ACTION_FIGURES:
            return API.getActionFigureByProductLine(productLine, filter);
        case LEGOS:
            return API.getFilteredLegosBy(filterAttribute, value);
        case MOVIES:
            return API.getFilteredMoviesBy(filterAttribute, value);     
        case VEHICLES:                
            return API.getAllVehiclesBy(filterAttribute, value);
        case VIDEO_GAMES:                
            return API.getFilteredVideoGameBySystem(value);
        default: 
            break;
        }
    }

    static getAllData = () => {
        return API.getAllToys()
            .concat(this.getAllMedia());
    }

    static getAllMedia = () => {
        return API.getFilteredMoviesBy()
            .concat(this.getFilteredVideoGameBySystem());
    }

    static getAllToys = () => {
        return API.getAllActionFiguresData()
            .concat(this.getAllVehicleData())
            .concat(this.getFilteredLegosBy());
    }

    static getActionFigureByProductLine = (productLine, filter) => {
        const { 
            filterAttribute, 
            value, 
        } = filter;

        switch(productLine) {
        case PRODUCT_LINES_OBJ.ALL:
            return API.getAllActionFiguresData();
        case PRODUCT_LINES_OBJ.BLACKSERIES:
            return API.getFilteredBlackSeriesBy(filterAttribute, value);
        case PRODUCT_LINES_OBJ.ELITESERIES:
            return API.getFilteredEliteSeriesBy(filterAttribute, value);
        default: 
            break;
        }
    }

    static getAllActionFiguresData = () => {
        return API.getFilteredBlackSeriesBy()
            .concat(this.getAllEliteSeriesData());
    }

    static getAllBlackSeriesData = () => {
        let id = 1;

        const result = BlackSeries2013n2014
            .concat(BlackSeries2014n2015)
            .concat(BlackSeries2015nPresent)
            .concat(BlackSeriesCenterpiece)
            .concat(BlackSeriesDeluxVehicle)
            .concat(BlackSeriesExclusives)
            .concat(BlackSeriesArchive);

        result.forEach(elem => {
            elem.id = `BS6.${id}`;

            elem.seriesName = 'Black Series 6"';
            elem.type = 'Action Figure';
            elem.versions.push('Regular');
        });
        return result;
    }

    static getFilterFiguresBy = (figures, filterAttribute, value) => {
        return filterAttribute 
            ? figures.filter(function (figure) {
                switch(filterAttribute){
                case DATA_ATTRIBUTES.PROD_ACTION_FIGURES.GROUP:
                    return figure.group.includes(value);
                case DATA_ATTRIBUTES.PROD_ACTION_FIGURES.VERSIONS:
                    return figure.versions.includes(value);
                default:
                    return figure[filterAttribute] === value;
                }
            })
            : figures;
    }

    static getFilteredBlackSeriesBy = (filterAttribute, value) => {
        const figures = API.getAllBlackSeriesData();
        return API.getFilterFiguresBy(figures,filterAttribute, value);
    }

    static getAllEliteSeriesData = () => {
        const result = EliteSeries;
        result.forEach(elem => {
            elem.seriesName = 'Elite Series';
            elem.type = 'Action Figure';
        });
        return result;
    }

    static getFilteredEliteSeriesBy = (filterAttribute, value) => {
        const figures = API.getAllEliteSeriesData();
        return API.getFilterFiguresBy(figures,filterAttribute, value);
    }

    static getAllLegoData = () => {
        const legoResults = Legos;
        legoResults.forEach(elem => {elem.type = 'LEGO';});
        return legoResults;
    }

    static getFilteredLegosBy = (filterAttribute, value) => {
        const legoData = API.getAllLegoData();
        return filterAttribute
            ? legoData.filter(function (lego) {
                return lego[filterAttribute] === value;
            })
            : legoData;
    }

    static getAllMovieData = () => {
        const result = Movies;
        result.forEach(elem => {elem.type = 'Movie';});
        return result;
    }

    static getFilteredMoviesBy = (filterAttribute, value) => {
        const movies = API.getAllMovieData();
        return filterDataBySingleNONArrayAttribute(movies, filterAttribute, value);
    }

    static getAllVideoGameData = () => {
        const result = VideoGames;
        result.forEach(elem => {elem.type = 'Video Game';});
        return result;
    }

    static getFilteredVideoGameBySystem = system => {
        const games = API.getAllVideoGameData();
        return system
            ? games.filter(function (game) {
                return game.systems.includes(system);
            })
            : games;
    }

    static getAllActionFleetData = () => {
        const result = ActionFleet;
        result.forEach(elem => {
            elem.type = 'Vehicle';
            elem.seriesName = 'Action Fleet';
        });
        return result;
    }

    static getFilteredActionFleetBy = (filterAttribute, value) => {
        const actionFleet = API.getAllActionFleetData();
        return filterDataBySingleNONArrayAttribute(actionFleet, filterAttribute, value);
    }

    static getAllVehicleData = () => {
        return API.getAllActionFleetData();
    }

    static getAllVehiclesBy = (filterAttribute, value) => {
        const vehicles = API.getAllVehicleData();
        return filterDataBySingleNONArrayAttribute(vehicles, filterAttribute, value);
    }
}

const filterDataBySingleNONArrayAttribute = (dataSet, filterAttribute, value) => { 
    return filterAttribute
        ? dataSet.filter(function (element) {
            return element[filterAttribute] === value;
        })
        : dataSet;
};
