import React from 'react';
import Grid from '@material-ui/core/Grid';
import { FILTER_LABELS } from '/constants/stringConstantsSelectors';
import { GET_SPECIFIC_ASSORTMENT } from '/constants/domainConstantSelectors';
import { SingleSelect } from '/common/input/selects/singleSelect';

export const AssortmentFilters = props => {

    const assortOptions = GET_SPECIFIC_ASSORTMENT(props.productLine);

    return (
        <Grid item xs={2}>
            <SingleSelect 
                handleFilterChange={props.handleFilterChangeByAssortment} 
                inputLabel={FILTER_LABELS.ASSORTMENT}
                menuItems={assortOptions}
            />
        </Grid>
    );

};
